    private void btnFatoraActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFatoraActionPerformed
        int numero = (int) txtNum.getValue();
        Fatorial f = new Fatorial();
        f.fatorial(numero);
        
        lblFormula.setText(f.getFormula() + "1 =");
        lblRes.setText(Integer.toString(f.getFatorial()));
    }//GEN-LAST:event_btnFatoraActionPerformed


